import Navbar from "./Navbar";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function QuestionCard() {
    const [questions, setQuestions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedLevel, setSelectedLevel] = useState("beginner");  // State for radio button selection
    const [showQuestions, setShowQuestions] = useState(false);  // State to show/hide questions
    const { id } = useParams();

    useEffect(() => {
        const token = localStorage.getItem('accessToken');
        fetch(`http://127.0.0.1:8000/quizapp/get_topics_id/${id}`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`,
            },
        })
        .then(res => {
            if (!res.ok) {
                throw new Error('Network response was not ok');
            }
            return res.json();
        })
        .then(data => {
            if (data.Questions.length === 0) {
                setError("No questions available for this topic.");
            } else {
                setQuestions(data.Questions);
                console.log(data.Questions);
            }
            setLoading(false);
        })
        .catch(err => {
            console.error("Error fetching questions:", err);
            setError(err.message);
            setLoading(false);
        });
    }, [id]);

    const handleLevelChange = (event) => {
        setSelectedLevel(event.target.value);
    };

    const handleStart = () => {
        setShowQuestions(true);  // Show questions when the start button is clicked
    };

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div>
            <Navbar />
            <div className="container">
                <h3>Select Level:</h3>
                <div className="form-group">
                    {/* Radio buttons for selecting level */}
                    <div className="form-check form-check-inline">
                        <input 
                            className="form-check-input" 
                            type="radio" 
                            name="levelOptions" 
                            id="beginner" 
                            value="beginner" 
                            checked={selectedLevel === "beginner"} 
                            onChange={handleLevelChange} 
                        />
                        <label className="form-check-label" htmlFor="beginner">
                            Beginner
                        </label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input 
                            className="form-check-input" 
                            type="radio" 
                            name="levelOptions" 
                            id="intermediate" 
                            value="intermediate" 
                            checked={selectedLevel === "intermediate"} 
                            onChange={handleLevelChange} 
                        />
                        <label className="form-check-label" htmlFor="intermediate">
                            Intermediate
                        </label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input 
                            className="form-check-input" 
                            type="radio" 
                            name="levelOptions" 
                            id="advanced" 
                            value="advanced" 
                            checked={selectedLevel === "advanced"} 
                            onChange={handleLevelChange} 
                        />
                        <label className="form-check-label" htmlFor="advanced">
                            Advanced
                        </label>
                    </div>
                </div>

                {/* Start Button */}
                <div className="text-end">
                    <button className="btn btn-primary" onClick={handleStart}>
                        Start
                    </button>
                </div>

                {/* Display questions only after clicking "Start" */}
                {showQuestions && (
                    <div className="container mt-3">
                        {questions.length > 0 ? (
                            questions.map(question => (
                                <p key={question.id}>{question.text}</p>
                            ))
                        ) : (
                            <p>No questions available for this topic.</p>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
}

export default QuestionCard;
