import { useState, useEffect } from "react";

function Session({ questions }) {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);  // Track the current question index
    const [timeLeft, setTimeLeft] = useState(20);  // Timer countdown state

    // Effect to handle countdown timer
    useEffect(() => {
        if (timeLeft === 0) {
            // Automatically move to the next question when time is up
            handleNextQuestion();
        }

        const timer = setInterval(() => {
            setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
        }, 1000);

        return () => clearInterval(timer);  // Clean up the timer on component unmount or question change
    }, [timeLeft]);

    // Function to handle moving to the next question
    const handleNextQuestion = () => {
        if (currentQuestionIndex < questions.length - 1) {
            setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
            setTimeLeft(20);  // Reset timer to 20 seconds for the next question
        } else {
            // Handle end of quiz logic here (e.g., show results or a completion message)
            alert("Quiz complete!");
        }
    };

    const currentQuestion = questions[currentQuestionIndex];  // Get the current question

    return (
        <div className="container mt-3 position-relative">
            <h3>Quiz Questions:</h3>

            {/* Countdown timer at the top right */}
            <div className="position-absolute top-0 end-0 p-3">
                <h5>Time Left: {timeLeft}s</h5>
            </div>

            {/* Display the current question */}
            <div key={currentQuestion.id} className="mb-3">
                <p>
                    <strong>Question {currentQuestionIndex + 1}:</strong> {currentQuestion.text}
                </p>
            </div>

            {/* Next button at the bottom right */}
            <div className="d-flex justify-content-end">
                <button className="btn btn-primary" onClick={handleNextQuestion}>
                    Next
                </button>
            </div>
        </div>
    );
}

export default Session;
